import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { ApiService } from '../shared/api.service';
import { StudentModel } from './student.model';


@Component({
  selector: 'app-student',
  templateUrl: './student.component.html',
  styleUrls: ['./student.component.css']
})
export class StudentComponent implements OnInit {

  studentValue!:FormGroup;
  studentObj:StudentModel= new StudentModel;
  studentList:any;

  constructor(private formBuilder: FormBuilder, private api: ApiService ) { }

  ngOnInit(): void{
       this.studentValue = this.formBuilder.group({
        name:[''],
        class:[''],
        email:[''],
        mobile:['']
       })
       this.AllStudent();
  }
  AddStudent(){
      this.studentObj.name = this.studentValue.value.name;
      this.studentObj.class = this.studentValue.value.class;
      this.studentObj.mobile = this.studentValue.value.mobile;
      this.studentObj.email = this.studentValue.value.email;

      this.api.postStudent(this.studentObj).subscribe({
        next: (v) => {
        console.log(v)
      },
      error: (e) =>{
        alert("Error")
        console.log(e)       
      },
      complete: () => {
        console.log("Student record added!")
        alert("Student record added!")
        this.AllStudent();
        this.studentValue.reset();

      }
    })
  }
    AllStudent(){
         this.api.getStudent().subscribe(res => {
          this.studentList = res;
         })
    }
  deleteStudent(data:any){
    this.api.deleteStudent(data.id).subscribe(

      {next: (v) => {
      console.log(v)
    },
    error: (e) =>{
      alert("Error")
      console.log(e)       
    },
    complete: () => {
      console.log("Student record deleted!")
      alert("Student record deleted!")
      this.AllStudent();
    }
    })
  }
  editStudent(data:any){
    this.studentValue.controls["name"].setValue(data.name);
    this.studentValue.controls["class"].setValue(data.class);
    this.studentValue.controls["email"].setValue(data.email);
    this.studentValue.controls["mobile"].setValue(data.mobile);
    this.studentObj.id = data.id;

  }

}
<div class="container">
    <nav class="navbar navbar-expand-lg navbar-light bg-primary">
        <div class="container-fluid">
          <a class="navbar-brand" href="#">Student Dashboard</a>
          <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <span class="navbar-toggler-icon"></span>
          </button>
          <div class="collapse navbar-collapse" id="navbarSupportedContent">

            <div class="d-flex">
              <button class="btn btn-success" data-bs-toggle="modal" data-bs-target="#staticBackdrop" type="submit">Add Student</button>
            </div>
          </div>
        </div>
      </nav>
      <table class="table table-striped table-hover">
        <thead>
            <tr>
                <th>Id</th>
                <th>Name</th>
                <th>Class</th>
                <th>Email</th>
                <th>Mobile</th>

            </tr>
        </thead>
        <tbody>
            <tr *ngFor="let student of studentList">
                <td>{{student.id}}</td>
                <td>{{student.name}}</td>
                <td>{{student.class}}</td>
                <td>{{student.email}}</td>
                <td>{{student.mobile}}</td>
                <td>
                    <input type="button" data-bs-toggle="modal" data-bs-target="#staticBackdrop" value="Edit" class="btn btn-success me-2"/>
                    <input type="button" value="Delete" (click)="deleteStudent(student)" class="btn btn-danger"/>

                </td>

            </tr>
        </tbody>
      </table>
</div>


<!-- Modal -->
<div class="modal fade" id="staticBackdrop" data-bs-backdrop="static" data-bs-keyboard="false" tabindex="-1" aria-labelledby="staticBackdropLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="staticBackdropLabel">Student Registration</h5>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
        <form [formGroup]="studentValue">
          <div class="mb-3">
            <label for="txtName" class="form-label">Name</label>
            <input type="text" formControlName="name" class="form-control" id="txtName" placeholder="Enter your name">
          </div>
          <div class="mb-3">
            <label for="txtClass" class="form-label">Class</label>
            <input type="text" formControlName="class" class="form-control" id="txtClass" placeholder="Enter your class">
          </div>
          <div class="mb-3">
            <label for="txtEmail" class="form-label">Email Id</label>
            <input type="email" formControlName="email" class="form-control" id="txtEmail" placeholder="Enter your email">
          </div>
          <div class="mb-3">
            <label for="txtMobile" class="form-label">Mobile</label>
            <div class="input-group">
              <div class="input-group-text">+216</div>
            <input type="text" formControlName="mobile" class="form-control" id="txtMobile" placeholder="Enter your mobile number">
          </div>
        </div>
        </form>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-warning" data-bs-dismiss="modal">Close</button>
        <button type="button"  class="btn btn-primary" (click)="AddStudent()">Register</button>
      </div>
    </div>
  </div>
</div>
